import React, { useState } from 'react';
import BWIPJS from 'bwip-js';
import { Modal, Button } from 'react-bootstrap';

const App = () => {
  const [link, setLink] = useState('');
  const [barcodeSrc, setBarcodeSrc] = useState('');
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);


  const generateBarcode = () => {
  console.log(link);
  const canvas = document.createElement('canvas');
  canvas.width = 400;  // Set the canvas width
  canvas.height = 400; // Set the canvas height
  BWIPJS.toCanvas(canvas, {
    bcid: 'qrcode',       // Barcode type
    text: link,           // Text to encode
                 // Adjust the scale to fit the barcode within the canvas
               // Bar height, in millimeters
    includetext: true,    // Show human-readable text
    textxalign: 'center', // Always good to set this
  }, (err) => {
    if (err) {
      // 'err' may be a string or Error object
      console.error(err);
    } else {

    }
  });
  const src = canvas.toDataURL('image/png');
      console.log(src);
      setBarcodeSrc(src);
  console.log('Barcode generated');
  handleShow();
};
  const styles = {
  container: {
    flex: 1,
    alignContent: 'center',
    height: 200,
    width: 200,
    padding: 20, 
  },
  image: {
    height: 300,
    width: 'auto'
  }
};

return (
  <div className="App">
    <input type="text" value={link} onChange={(e) => setLink(e.target.value)} />
    <button onClick={generateBarcode}>Generate Barcode</button>

    {barcodeSrc &&

   <Modal 
  show={show} 
  onHide={handleClose}
  style={{ 
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  }}
  dialogClassName="modal-90w"
>
  <Modal.Dialog style={{ maxWidth: '400px', margin: '0 auto' }}>
    <Modal.Header closeButton style={{ backgroundColor: '#f8f9fa', borderBottom: '1px solid #dee2e6' }}>
      <Modal.Title>Scan Image!</Modal.Title>
    </Modal.Header>
    <Modal.Body style={{ 
  backgroundColor: '#f8f9fa', 
  display: 'flex', 
  justifyContent: 'center', 
  alignItems: 'center', 
  padding: '20px' 
}}>
  <div style={{...styles.container, maxWidth: '90%', textAlign: 'center'}}>
    <img src={barcodeSrc} alt="Barcode" style={{...styles.image, maxWidth: '100%'}} />
  </div>
</Modal.Body>
    <Modal.Footer style={{ 
  backgroundColor: '#f8f9fa', 
  borderTop: '1px solid #dee2e6', 
  position: 'absolute', 
  bottom: 0, 
  width: '100%', 
  justifyContent: 'center' 
}}>
  <Button variant="secondary" onClick={handleClose}>
    OK
  </Button>
</Modal.Footer>
  </Modal.Dialog>
</Modal>
    }



  </div>
);
};

export default App;
